import React from 'react';
import proj1 from '../assets/proj1.png';
import proj2 from '../assets/proj2.png';
import proj3 from '../assets/proj3.png';
import proj4 from '../assets/proj4.png';
import proj5 from '../assets/proj5.png';
import proj6 from '../assets/proj6.png';

const Projects = () => {
  return (
    <div className='max-w-[1200px] mx-auto p-5'>
        <div className='pb-8'>
            <p className='text-4xl mb-3 font-bold primary-color'>Projects</p>
            <p className='text-gray-400'>Check out some of my projects</p>
        </div>
        <div className='grid grid-cols-2 md:grid-cols-3 gap-4'>
            <ProjectCard image={proj1} title="Project 1" githubLink="https://github.com/youssef-gh/" />
            <ProjectCard image={proj2} title="Project 2" githubLink="https://github.com/youssef-gh/" />
            <ProjectCard image={proj3} title="Project 3" githubLink="https://github.com/youssef-gh/" />
            <ProjectCard image={proj4} title="Project 4" githubLink="https://github.com/youssef-gh/" />
            <ProjectCard image={proj5} title="Project 5" githubLink="https://github.com/youssef-gh/" />
            <ProjectCard image={proj6} title="Project 6" githubLink="https://github.com/youssef-gh/" />
        </div>
    </div>
  );
};

const ProjectCard = ({ image, title, githubLink }) => {
    return (
        <div className='transform transition-transform duration-300 hover:scale-105 overflow-hidden
            shadow-lg shadow-[#040c16] group  rounded-md flex justify-center items-center
            h-[200px] bg-cover relative'>
            <img src={image} layout="fill" objectFit="cover" alt={title} />
            <div className='opacity-0 group-hover:opacity-90 bg-[gray/70] absolute inset-0 flex flex-col justify-center items-center'>
                <span className='text-2xl font-bold text-white tracking-wider'>
                    {title}
                </span>
                <div className='pt-8 text-center'>
                    <a href={githubLink}>
                        <button className='text-center rounded-lg px-4 py-3 m-2 bg-white text-gray-700 font-bold text-lg'>Code</button>
                    </a>
                </div>
            </div>
        </div>
    );
};

export default Projects;
